import React, { useState } from 'react' 
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faBars} from '@fortawesome/free-solid-svg-icons'
import {useTransition, animated} from 'react-spring'
import{
    BrowserRouter as Router,
    Link,
    Switch,
    Route
  } from "react-router-dom"


function Navigation(){
    const [showMenu,setShowMenu] = useState(false)

    const maskTransitions = useTransition(showMenu, null, {
        from: { position: 'absolute', opacity: 0 },
        enter: { opacity: 1 },
        leave: { opacity: 0 },
        })

        const menuTransitions = useTransition(showMenu, null, {
            from: { opacity:0, transform:'translateX(-100%' },
            enter: { opacity: 1, transform: 'translateX(0%)' },
            leave: { opacity: 0, transform: 'translateX(-100%' },
            })

    return (
        <nav>
            <span className='text-xl'>
                <FontAwesomeIcon
                    icon={faBars}
                    onClick={() => setShowMenu(!showMenu)}
                />
            </span>
            {
                maskTransitions.map(({ item, key, props }) =>
                item && 
                <animated.div 
                    key={key} 
                    style={props}
                    className='bg-black-t-50 fixed top-0 left-0 w-full h-full z-50'
                    onClick={()=>setShowMenu(false)}
                >
                </animated.div>
                )
            }
            {
                menuTransitions.map(({ item, key, props }) =>
                item && 
                <animated.div 
                    key={key} 
                    style={props}
                    className='fixed bg-white top-0 left-0 w-1/5 h-full z-50 shadow p-3'
                >
                    <span className="text-xl">
                        CATEGORY
                    </span>
                    <Router>
                        <ul className="text-sl">
                            <li>
                                <Link to='/' className="text-blue-500">Home</Link>
                            </li>
                            <li>
                                <Link to='/cafe' className="text-blue-500">Cafe</Link>
                            </li>
                            <li>
                                <Link to='/restaurant' className="text-blue-500">Restaurant</Link>
                            </li>
                            <li>
                                <Link to='/activity' className="text-blue-500">Activity</Link>
                            </li>
                        </ul>
                        <Switch>
                            <Route exact path="/"></Route>
                            <Route path="/cafe"></Route>
                            <Route path="/restaurant"></Route>
                            <Route path="/activity"></Route>
                        </Switch>
                    </Router>

                </animated.div>
                )
            }
        </nav>
    )
}

export default Navigation
