import React from 'react'
import { useHistory } from "react-router-dom";


export const Delete = ({id})=>{
    const history = useHistory()
    const url = `http://127.0.0.1:5000/api/v1/delete/${id}`
    const deleteTodo =()=>{
        if(confirm){
            fetch(url,{
                method:'POST',
                body: JSON.stringify({
                    id: id
                })
            }).then(response=>response.json())
              .then(data=>{
                  console.log(data)
                  history.push('/')
              })
            }
        }

    return (
        <div className="text-center">
            <button onClick={deleteTodo} className="border border-gray-700 bg-gray-700 text-white rounded-md px-4 py-2  transition duration-500 ease select-none hover:bg-red-800 focus:outline-none focus:shadow-outline">Delete</button>
        </div>
    )
}

export default Delete
