import React from 'react'

function Card(props){
    return (
        <div className='border mb-4 rounded'>
            <div 
            style = {{
                'backgroundImage': `url('${props.info.image}')`
            }}
            className='w-full h-64 bg-blue bg-cover'
            >
            </div>
            <div className="p-3">
                <h3 className="fond-bold text-xl mb-3">
                    {props.info.title}
                </h3>
                <p>
                    {props.info.description}
                </p>

                <a href = {props.info.map} className= 'm-3 border border-indigo-500 text-indigo-500 rounded-md px-4 py-2 m-2 transition duration-500 ease select-none hover:text-white hover:bg-indigo-600 focus:outline-none focus:shadow-outline'>
                    <button>GoogleMap</button>
                </a>


            </div>

        </div>
    )
}

export default Card
