import React, { useEffect, useState } from 'react'
import EditCard from '../Components/EditCard'
import {
    useParams,
    Link
  } from "react-router-dom";

function Edit(){
    const {id} = useParams()
    const url = `http://127.0.0.1:5000/api/v1/id/${id}`
    const [data, setData] = useState([]);

    useEffect(()=>
    fetch(url)
    .then(response => response.json())
    .then(data=>setData(data)
    ),[] )

    let content = null

    console.log(data.result)
    if(data.result){
        content = (
            <div>
                <EditCard info = {data.result}/>
            </div>

        )
    }

    return(
        <div>
            <section className=" text-gray-200 bg-gray-900">
                <div className="max-w-6xl mx-auto px-5 py-24 max-h-screen	">
                    <div className="flex flex-wrap w-full mb-1 flex-col items-center text-center">
                        <h1 className=" title-font mb-5 text-4xl font-extrabold leading-10 tracking-tight text-left sm:text-5xl sm:leading-none md:text-6xl"> Edit Page</h1>



                        {content}
                        <Link to='/'>
                            <button>Back</button>
                        </Link>


                    </div>
                </div>
            </section>
        </div>
    )
}

export default Edit
