import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Card from '../Components/Card'

function Activity(){

    const url = 'http://127.0.0.1:5000/api/v1/activity'
    const [activities,setActivities] = useState({},[url])

    useEffect(()=>{
        axios.get(url)
            .then(response =>{
                setActivities(response.data)
            })
    }, [url])

    let content = null

    if(activities.result){
        content =  
            activities.result.map((cafe,key)=>
                <div key = {key}>
                    <Card 
                        info = {cafe}
                    />
                </div>
            )

    }

    return(
        <div>
            <section class=" text-gray-200 bg-gray-900">
            <div class="max-w-6xl mx-auto px-5 py-24 ">
                <div class="flex flex-wrap w-full mb-20 flex-col items-center text-center">
                <h1 class=" title-font mb-2 text-4xl font-extrabold leading-10 tracking-tight text-left sm:text-5xl sm:leading-none md:text-6xl"> Favorite Restaurant</h1>
                </div>
                <div class="flex flex-wrap -m-4">

                {content}

                </div>
            </div>
            </section>
        </div>

    )
}

export default Activity
