import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Card from '../Components/Card'

function Cafe(){
    const url = 'http://127.0.0.1:5000/api/v1/cafe'
    const [cafes,setCafes] = useState({
        loading: false,
        data:null,
    },[url])

    useEffect(()=>{
        setCafes({
            loading:true,
            data:null
        })
        axios.get(url)
            .then(response =>{
                setCafes({
                    loading: false,
                    data: response.data
                })
            })
    }, [url])

    let content = null

    if(cafes){
        content =  
            cafes.map((cafe,key)=>
                <div key = {key}>
                    <Card 
                        info = {cafe}
                    />
                </div>
            )
    }

    return(
        <div>

            <h1>My favorite cafe</h1>
            {content}

        </div>

    )
}

export default Cafe
