import React from 'react'
import {Link} from 'react-router-dom'
import { useHistory } from "react-router-dom";

function EditCard(props){
    const history = useHistory()
    const url = `http://127.0.0.1:5000/api/v1/delete/${id}`
    const deleteCard =()=>{
        fetch(url,{
            method:'POST',
            body: JSON.stringify({
                id: id
            })
        }).then(response=>response.json())
          .then(data=>{
              console.log(data)
              history.push('/')
          })
    }

    return (
                <div className="border border-gray-300 p-6 rounded-lg ">

                    <img src = {props.info.image} />
                    <h2 className="text-lg  font-medium title-font mb-2">{props.info.title}</h2>
                    <p className="leading-relaxed text-base">{props.info.description}</p>

                    <div className="google-map-code mt-3">
                        <iframe src= {props.info.map} aria-hidden="false" ></iframe>
                    </div>

                    <button type="button" onClick={deleteCard} className="border border-gray-700 bg-gray-700 text-white rounded-md px-4 py-2 m-2 transition duration-500 ease select-none hover:bg-gray-800 focus:outline-none focus:shadow-outline">
                        Delete
                    </button>

                    {/* <Link to={`delete/${props.info._id.$oid}`}>   
                        <button type="button" className="border border-gray-700 bg-gray-700 text-white rounded-md px-4 py-2 m-2 transition duration-500 ease select-none hover:bg-gray-800 focus:outline-none focus:shadow-outline">
                            Delete
                        </button>
                    </Link> */}


                </div>
    )
}

export default EditCard
